import { NavLink, useLocation } from "react-router-dom";
import { Plus, List, User, LogOut } from "lucide-react";
import { useAuth } from "@/hooks/useAuth";
import { Button } from "@/components/ui/button";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";

const items = [
  { title: "Nova PRT", url: "/nova-prt", icon: Plus },
  { title: "Lista de Produtividade", url: "/lista", icon: List },
];

export function AppHeader() {
  const location = useLocation();
  const { user, signOut } = useAuth();
  const currentPath = location.pathname;

  const isActive = (path: string) => currentPath === path;
  const getNavCls = ({ isActive }: { isActive: boolean }) =>
    isActive 
      ? "bg-green-600 text-white font-medium hover:bg-green-700" 
      : "hover:bg-green-50 hover:text-green-700 text-gray-700";

  return (
    <header className="h-16 flex items-center justify-between border-b bg-white shadow-sm px-6">
      {/* Logo e título */}
      <div className="flex items-center gap-4">
        <div className="flex items-center gap-3">
          {/* Logo */}
          <img src="/logo.svg" alt="Fiscal Score Logo" className="w-10 h-10" />
          <div>
            <h1 className="font-bold text-xl text-gray-800">Fiscal Score</h1>
            <p className="text-sm text-gray-500">Sistema de Gestão</p>
          </div>
        </div>
      </div>

      {/* Menu de navegação */}
      <nav className="flex items-center gap-2">
        {items.map((item) => (
          <NavLink
            key={item.title}
            to={item.url}
            end
            className={({ isActive }) =>
              `flex items-center gap-2 px-4 py-2 rounded-lg transition-colors ${getNavCls({ isActive })}`
            }
          >
            <item.icon className="h-4 w-4" />
            <span>{item.title}</span>
          </NavLink>
        ))}
      </nav>

      {/* Menu do usuário */}
      <div className="flex items-center gap-4">
        <DropdownMenu>
          <DropdownMenuTrigger asChild>
            <Button variant="ghost" className="flex items-center gap-2 hover:bg-green-50">
              <User className="h-4 w-4" />
              <span className="hidden sm:inline">{user?.email}</span>
            </Button>
          </DropdownMenuTrigger>
          <DropdownMenuContent align="end" className="w-56">
            <DropdownMenuItem onClick={signOut} className="text-red-600 hover:text-red-700">
              <LogOut className="h-4 w-4 mr-2" />
              Sair
            </DropdownMenuItem>
          </DropdownMenuContent>
        </DropdownMenu>
      </div>
    </header>
  );
}
