import { useAuth } from './useAuth';

export function useAdmin() {
  const { user } = useAuth();
  
  const isAdmin = user?.email === 'kezia@fiscal.com';
  
  return {
    isAdmin,
    adminEmail: 'kezia@fiscal.com'
  };
}
